import React, { useState, useEffect } from 'react';
import './App.css';
import CatCard from './components/CatCard';
import CatFacts from './components/CatFacts';
import SearchBar from './components/SearchBar';

function App() {
  const [cats, setCats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [favorites, setFavorites] = useState([]);

  // The Cat APIÏóêÏÑú Í≥†ÏñëÏù¥ Ïù¥ÎØ∏ÏßÄ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    fetchCats();
  }, []);

  const fetchCats = async () => {
    setLoading(true);
    try {
      const response = await fetch('https://api.thecatapi.com/v1/images/search?limit=12');
      const data = await response.json();
      setCats(data);
    } catch (error) {
      console.error('Í≥†ÏñëÏù¥Î•º Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§:', error);
    }
    setLoading(false);
  };

  const toggleFavorite = (catId) => {
    setFavorites(prev => {
      if (prev.includes(catId)) {
        return prev.filter(id => id !== catId);
      } else {
        return [...prev, catId];
      }
    });
  };

  const filteredCats = cats.filter(cat =>
    cat.id.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="App">
      <header className="App-header">
        <h1>üê± Í≥†ÏñëÏù¥ Í∞§Îü¨Î¶¨ üê±</h1>
        <p className="subtitle">The Cat APIÎ•º ÏÇ¨Ïö©Ìïú Í∑ÄÏó¨Ïö¥ Í≥†ÏñëÏù¥Îì§</p>
      </header>

      <div className="container">
        <div className="controls">
          <SearchBar searchTerm={searchTerm} onSearchChange={setSearchTerm} />
          <button className="refresh-btn" onClick={fetchCats}>
            üîÑ ÏÉàÎ°úÏö¥ Í≥†ÏñëÏù¥ Î≥¥Í∏∞
          </button>
        </div>

        {favorites.length > 0 && (
          <div className="favorites-count">
            ‚ù§Ô∏è Ï¶êÍ≤®Ï∞æÍ∏∞: {favorites.length}Í∞ú
          </div>
        )}

        {loading ? (
          <div className="loading">
            <div className="spinner"></div>
            <p>Í∑ÄÏó¨Ïö¥ Í≥†ÏñëÏù¥Îì§ÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
          </div>
        ) : (
          <div className="cat-grid">
            {filteredCats.map((cat) => (
              <CatCard
                key={cat.id}
                cat={cat}
                isFavorite={favorites.includes(cat.id)}
                onToggleFavorite={toggleFavorite}
              />
            ))}
          </div>
        )}

        {!loading && filteredCats.length === 0 && (
          <div className="no-results">
            <p>Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§ üòø</p>
          </div>
        )}

        <CatFacts />
      </div>
    </div>
  );
}

export default App;
